using System.Collections.Generic;
using Iota.Lib.Api.Model;
using Iota.Lib.Api.Utils;
using Microsoft.VisualStudio.TestTools.UnitTesting;

namespace Iota.Lib.Test
{
    [TestClass]
    public class InputValidatorTest
    {
        readonly string TEST_ADDRESS_WITH_CHECKSUM =
            "PNGMCSNRCTRHCHPXYTPKEJYPCOWKOMRXZFHH9N9VDIKMNVAZCMIYRHVJIAZARZTUETJVFDMBEBIQE9QTHBFWDAOEFA";

        readonly string TEST_TRYTES =
            "";

        readonly string TEST_HASH = "OAATQS9VQLSXCLDJVJJVYUGONXAXOFMJOZNSYWRZSWECMXAQQURHQBJNLD9IOFEPGZEPEMPXCIVRX9999";

        private static string TEST_MESSAGE = "JOTA";
        private static string TEST_TAG = "JOTASPAM9999999999999999999";

        [TestMethod]
        public void TestIsTrytes()
        {
            Assert.IsTrue(InputValidator.IsTrytes(TEST_TRYTES, TEST_TRYTES.Length));
        }

        [TestMethod]
        public void TestIsValue()
        {
            Assert.IsTrue(InputValidator.IsValue("1234"));
            Assert.IsTrue(InputValidator.IsValue("-1234"));
            Assert.IsFalse(InputValidator.IsValue("-"));
            Assert.IsFalse(InputValidator.IsValue(string.Empty));
            Assert.IsFalse(InputValidator.IsValue("     "));
            Assert.IsFalse(InputValidator.IsValue("1223 7"));
            Assert.IsFalse(InputValidator.IsValue("1337 A"));
        }

        [TestMethod]
        public void TestIsArrayOfHashes()
        {
            Assert.IsTrue(InputValidator.IsArrayOfHashes(new string[] {TEST_HASH, TEST_HASH}));
        }

        [TestMethod]
        public void TestIsTransfersCollectionValid()
        {
            List<Transfer> transfers = new List<Transfer>
            {
                new Transfer(TEST_ADDRESS_WITH_CHECKSUM, 0, TEST_MESSAGE, TEST_TAG),
                new Transfer(TEST_ADDRESS_WITH_CHECKSUM, 0, TEST_MESSAGE, TEST_TAG)
            };
            Assert.IsTrue(InputValidator.IsTransfersCollectionValid(transfers));
        }
    }
}