using Microsoft.VisualStudio.TestTools.UnitTesting;
using Iota.Lib.Utils;

namespace Iota.Lib.Test
{
    [TestClass]
    public class PearlDiverTest
    {
        const string NODE = "nodes.thetangle.org"; //Your test node; Please note that not all nodes accept the all requests
        const int PORT = 443;                      //Your test nodes's port
        const bool IS_SSL = true;                  //Your test node's encryption state

        PearlDiver pd = new PearlDiver();
        IotaApi api = new IotaApi(NODE, PORT, IS_SSL);
        //string RAW_TRANSACTION = "";

        [TestMethod]
        public void TestPearDiver_01()
        {
            var tips = api.GetTipsAsync().Result;
            var raw_transaction = api.GetTrytesAsync(tips.Hashes[0]).Result.Trytes[0];
            int[] trits = Converter.ConvertTrytesToTrits(raw_transaction);
            ArrayUtils.PadArrayWithZeros(trits, trits.Length + (trits.Length % Constants.RADIX));
            Assert.IsTrue(trits.Length == 8019);
            var success = pd.Search(trits, Constants.MIN_WEIGHT_MAGNITUDE);
            Assert.IsTrue(success);
        }

    }
}
